mapper.select:
select ta.*,tb.resource_mark,tb.resource_name,tb.resource_url from platform_index_config ta left join platform_resource tb on ta.resource_id = tb.id
ta.resource_id IS NOT NULL AND ta.resource_id IN ( SELECT resource_id FROM platform_role_resource WHERE role_id in
#{id_item}
)
order by ta.config_order

select id resource_id,resource_mark,resource_name from platform_resource
id not in( select resource_id from platform_index_config
and resource_id is not null and ta.user_id = #{userId,jdbcType=BIGINT} and ta.role_id = #{roleId,jdbcType=BIGINT}
) and index_div_config = #{indexDivConfig,jdbcType=TINYINT}
